package com.wip.training.exc6;
import java.util.Arrays;
public class SortOutCountries {

  public static void main(String[] args) {
        // TODO Auto-generated method stub
        String countryArray[] = {"Japan", "Austraila", "China", "India", "USA", "Thailand", "Sri Lanka"};

        Arrays.sort(countryArray);
        System.out.println("Countries sorted list =" + Arrays.toString(countryArray));
  }

}
